flag_binary_no_spaces = ""

def encrypt(binary_msg, pub):
    if (len(binary_msg) % len(pub)) != 0:
        print(f"invalid binary, please make it a multiple of {len(pub)} but is currently {len(binary_msg)}")
    else:
        encrypted_characters = []
        encrypted_character = 0
        for index in range(0, len(binary_msg)):
            if index == (len(binary_msg) - 1) or index != 0 and index % len(pub) == 0:
                encrypted_characters.append(encrypted_character)
                encrypted_character = 0
            bit = int(binary_msg[index])
            encrypted_character = encrypted_character + (bit * pub[(index % len(pub))])
        return encrypted_characters

def decrypt(ciphertext, m, n_inv):
    plaintext = []
    if type(ciphertext) == list:
        for value in ciphertext:
            numeral = (int(value) * n_inv) % m
            binary = int_to_binary(numeral)
            plaintext.append(binary)
    elif type(ciphertext) == int:
        numeral = (ciphertext*n_inv) % m
        binary = int_to_binary(numeral)
        plaintext.append(binary)
    return plaintext

def int_to_binary(integer):
    binary = ''
    total = integer
    index = len(priv) - 1
    while index >= 0:
        if total >= priv[index]:
            binary = '1' + binary
            total = total - priv[index]
        else:
            binary = '0' + binary
        index -= 1
    if total > 0:
        return 'error'
    else:
        return binary
    return binary

def main():
    print("flag:", flag_binary_no_spaces)
    ciphertext = (encrypt(flag_binary_no_spaces, pub))
    print("ciphertext:", ciphertext)
    plaintext = (decrypt(ciphertext, m, n_inv))
    print("decrypted:", ''.join(plaintext))




